/*!
* jQuery Table row and column Highlighter 
*/
$.fn.rowHighlighter = function(state,obj) {         
    var element = $('tr[aria-custom-tr="'+obj+'"]').first();    
    if(state){
      $(element).addClass("box");
      $('html, body').animate({
        scrollTop: ($(element).first().offset().top - 250) + 'px'                 
      }, 100); 
    }else{
      setTimeout(function(){ $(element).removeClass("box").fadeIn(10000); }, 5000);
    }
  }